        <link rel='stylesheet' type="text/css" href="style/layout.css">
        <style>
            *:focus {
                outline: none;
                -moz-box-shadow: none;
                -webkit-box-shadow: none;
                box-shadow: none;
            }
            body { padding: 40px; font-family: 'Lato', sans-serif; font-size: 14px; font-weight: 300; }
            section div.text_box .file { margin: 0; padding-bottom: 5px; }
            section div.text_box { color: #999999; }
            
            section div.text_box ul { list-style: none; }
            section div.text_box ul li { float: left; width: 180px; text-align: center; }
            section div.text_box ul li figure { width: 100px; height: 100px; background-position: center; background-size: contain; background-repeat: no-repeat; box-shadow: 0 0 10px #aaaaaa; border-radius: 4px; transition: 0.5s; }
            section div.text_box p { font-size: 0.85em; }
            section div.text_box .size { font-size: 0.75em; opacity: 0.6; }
            
            section a { color: #000000; text-decoration: none; }
            section a:hover p { text-decoration: underline; color: #36c6be; }
            section a:hover figure { opacity: 0.6; }
            
            summary { font-weight: bold; margin-bottom: 5px; cursor: pointer;}
            details { margin-top: 10px; padding: 10px; border: 1px dashed #aaaaaa; border-radius: 5px; overflow: hidden; }
            
            footer { width: 100%; text-align: center; font-size: 0.85em; color: #888888; }
            footer a { color: inherit; text-decoration: none; transition: 0.5s; }
            footer a:hover { color: #444444; }
            
        </style>
